import React from 'react';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import Link from 'next/link';
import Calendar from './calendar';

// style
const logo = css`
  font-size: 40px;
  color: #fc4a1a;
  display: inline-block;
  margin-left: 20px;
  margin-top: 20px;
  font-family: 'Karla', sans-serif;
  text-decoration: none;
  border: 2px solid #fc4a1a;
`;

const app = css`
  min-height: 100vh;
  font-weight: 400;
  font-family: 'Karla', sans-serif;
  width: 100%;
  position: absolute;
`;

const mainTitle = css`
  color: #f7b733;
  font-family: 'Karla', sans-serif;
  text-align: center;
  letter-spacing: 0.1em;
`;

const readyToGo = css`
  color: #f7b733;
  font-size: 20px;
  text-decoration: none;
  font-family: 'Karla', sans-serif;
`;

const li = css`
  margin: 20px;
  list-style-type: none;
  font-family: 'Karla', sans-serif;
`;

const calendar = css`
  margin-left: 20px;
`;

const main = css`
  display: flex;
  flex-direction: row;
`;

const img = css`
  width: 50%;
  border-radius: 4px;
  margin-bottom: 10px;
  margin-left: 20px;
`;

const description = css`
  font-family: 'Karla', sans-serif;
  list-style-type: none;
  margin: 20px;
  width: 47%;
  text-align: left;
  background: #f7b733;
  padding: 20px;
  border-radius: 4px;
  line-height: 1.8;
`;

export default function interests(props) {
  // fetching data
  fetch(
    'https://data.wien.gv.at/daten/geo?service=WFS&request=GetFeature&version=1.1.0&typeName=ogdwien:SPAZIERPUNKTOGD &srsName=EPSG:4326&outputFormat=json',
  )
    .then((res) => res.json())
    .then((json) => console.log(json));

  return (
    <>
      <div css={app}>
        <Link href={'/index'}>
          <a href="#a" css={logo}>
            Wolfi
          </a>
        </Link>
        <h1 css={mainTitle}>Get your plan here</h1>
        <div css={main}>
          <div css={calendar}>
            <Calendar />
          </div>
          <div>
            <ul>
              {props.sights.map((sight) => {
                return (
                  <>
                    <li key={sight.id} css={li}>
                      Name: {sight.name}
                    </li>
                    <li key={sight.id} css={li}>
                      Address: {sight.address}
                    </li>
                    <img
                      key={sight.id}
                      src={sight.image}
                      alt={sight.name}
                      css={img}
                    />
                    <li css={description}>{sight.description}</li>
                  </>
                );
              })}
            </ul>
          </div>
        </div>
        <div>
          <Link href={'/readyToGo'}>
            <a href="#a" css={readyToGo}>
              Get your plan! &#x0226B;
            </a>
          </Link>
        </div>
      </div>
    </>
  );
}

export async function getServerSideProps(context) {
  const { getSights } = await import('../db.js');

  const sights = await getSights();
  return {
    props: {
      sights,
    },
  };
}
