import React from 'react';
import Head from 'next/head';
import Link from 'next/link';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import { GetServerSidePropsContext } from 'next';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';

//================================================================================
// Style
//================================================================================

const app = css`
  background: #4abdac;
  min-height: 100vh;
  font-weight: 400;
  font-family: 'Karla', sans-serif;
  width: 100%;
  position: absolute;
`;
const form = css`
  background: #f7b733;
  width: 200px;
  position: relative;
  margin: auto;
  margin-top: 10%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
  border-radius: 6px;
`;

const input = css`
  line-height: 2.5;
  margin-bottom: 15px;
  border-radius: 4px;
  border: 2px solid #f7b733;
  margin-top: 20px;
  font-family: 'Karla', sans-serif;
`;

const button = css`
  border-radius: 4px;
  font-family: 'Karla', sans-serif;
  font-size: 18px;
  margin-top: 10px;
  background: #ffff;
  border: 2px solid #2f4f4f;
`;

const forgottenPass = css`
  text-align: center;
  color: #323339;
  font-size: 18px;
  font-family: 'Karla', sans-serif;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 15px;
  text-decoration: none;
`;

const logo = css`
  font-size: 40px;
  color: #fc4a1a;
  display: inline-block;
  margin-left: 20px;
  margin-top: 20px;
  font-family: 'Karla', sans-serif;
  text-decoration: none;
  border: 2px solid #fc4a1a;
`;

type Props = {
  csrfToken: string;
};

//================================================================================
// Function
//================================================================================
export default function Login(props: Props) {
  return (
    <>
      <div css={app}>
        <Link href={'/index'}>
          <a href="#a" css={logo}>
            Wolfi
          </a>
        </Link>
        <form method="POST" css={form}>
          <input css={input} placeholder="Name" type="name" name="userName" />
          <input type="hidden" name="csrf" value={props.csrfToken} />
          <input
            css={input}
            placeholder="Password"
            type="password"
            name="customerPassword"
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
          />
          <button css={button} type="button">
            {' '}
            LOG IN
          </button>
        </form>
        <a href="#a" css={forgottenPass}>
          I forgot my password, send me an email &#x0226B;
        </a>
        <Link href={'/signup'}>
          <a href="#a" css={forgottenPass}>
            Sign up &#x0226B;
          </a>
        </Link>
      </div>
    </>
  );
}

export async function getServerSideProps(context: GetServerSidePropsContext) {
  let buffer = '';

  context.req.on('data', (chunk) => {
    buffer += chunk;
  });

  context.req.on('end', () => {
    console.log(Buffer.from(buffer).toString());
    // console.log(queryString.parse(Buffer.from(buffer).toString()));
  });

  const props: Props = {
    csrfToken: 'TODO: Add real CSRF token here',
  };

  return {
    props,
  };
}
