import React from 'react';
import Link from 'next/link';
import Header from '../../components/Header';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import Footer from '../../components/Footer';

export default function Userplan({ plan }) {
  const app = css`
    box-sizing: content-box;
    padding: 0;
    margin: 0;
  `;

  const header = css`
    width: 100%;
    margin-top: 20px;
    text-align: center;
  `;

  const quote = css`
    font-size: 2rem;
    margin-top: 10px;
    margin-bottom: 10px;
    color: #555555;
    display: inline-block;
    font-family: 'Karla', cursive;
  `;

  const journey = css`
    width: 100%;
    margin: auto;
  `;

  const text = css`
    font-family: 'Karla', cursive;
    font-size: 20px;
    line-height: 2;
    list-style-type: none;
    padding: 10px;
    color: #555555;
  `;

  const name = css`
    font-family: 'Karla', cursive;
    font-size: 25px;
    line-height: 1.2;
    list-style-type: none;
    padding: 10px;
    color: #555555;
    border-bottom: 1.5px solid #4abdac;
  `;

  const img = css`
    width: 50%;
    background-color: $coral;
    -webkit-box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    -moz-box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    animation: slideUp 2000ms ease;
    margin-bottom: 20px;
    border-radius: 4px;
  `;

  const main = css`
    min-height: 100vh;
    padding: 0 0.5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 5%;
    width: 50%;
    display: flex;
    justify-content: center;
    background-color: #ffff;
    -webkit-box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    -moz-box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    box-shadow: 9px 13px 25px 0px rgba(0, 0, 0, 0.18);
    animation: slideUp 2000ms ease;
    margin-bottom: 20px;
    border-radius: 4px;
    margin: auto;
  `;

  const description = css`
    list-style-type: none;
    border-radius: 4px;
    padding: 10px;
    margin-top: 10px;
    font-family: 'Karla', sans-serif;
    color: #555555;
    line-height: 2;
    text-align: center;
  `;

  const icon = css`
    width: 5%;
    margin-left: 10px;
  `;

  const li = css`
    display: flex;
    flex-direction: column;
    justify-content: center;
  `;
  return (
    <div>
      <div>
        <link
          href="https://fonts.googleapis.com/css2?family=Comfortaa:wght@300&family=Karla&display=swap"
          rel="stylesheet"
        ></link>
      </div>
      <div css={app}>
        <Header />
        <div css={main}>
          <div css={header}>
            <span css={quote}>Your plan </span>
            <img css={icon} src="/yellowMap.png" alt="goal-flag" />
          </div>
          <div css={journey}>
            <ul>
              {plan.map((value) => {
                return (
                  <li key={value.name} css={li}>
                    <span css={name}>{value.name}</span>
                    <span css={text}>
                      <strong>Address:</strong> {value.address}
                    </span>
                    <img css={img} src={value.image} alt={value.name} />
                    <p css={description}>{value.description}</p>
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
}

export async function getServerSideProps(context) {
  const { personalizedPlan } = await import('../../db.js');
  const plan = await personalizedPlan(context.params.id);
  console.log('this', plan);

  if (plan === null) {
    return { props: {} };
  }
  return {
    props: {
      plan,
    },
  };
}
