import React, { useEffect } from 'react';
import Link from 'next/link';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';

export default function Header() {
  const logo = css`
    font-size: 30px;
    color: #24305e;
    padding-left: 20px;
  `;

  const navbar = css`
    width: 100%;
    display: flex;
    color: #24305e;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    background: #ffff;
    font-family: 'Julius Sans One', sans-serif;
    padding: 20px;
  `;

  const links = css`
    padding-right: 30px;
    padding-left: 20px;
    font-size: 1.5rem;
  `;

  const linkList = [{ name: 'About', url: '/about' }];

  if (user === null) {
    linkList.push({ name: 'Register', url: '/register' });
    linkList.push({ name: 'Login', url: '/login' });
  } else {
    linkList.push({ name: 'Logout', url: '/logout' });
  }

  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch('/api/checkLogin', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      // body: JSON.stringify({ username, password }),
    })
      .then((response) => {
        console.log('success', response);
        if (response === 'true') {
          setUser('TODO: add the user');
        }
      })
      .catch((err) => {
        console.log('error fetching session');
      });
  }, []);

  return (
    <div>
      <style>
        @import
        url('https://fonts.googleapis.com/css2?family=Major+Mono+Display&display=swap');
      </style>
      <div css={navbar}>
        <div className="logo-header">
          <a href="#a" css={logo}>
            Wolfi
          </a>
        </div>
        <div className="navbar-linkList">
          {linkList.map((link) => {
            return (
              <Link key={link.url} href={link.url}>
                <a css={links}>{link.name}</a>
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
}
