import React, { useState } from 'react';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import Link from 'next/link';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

export default function () {
  const iframe = css`
    border: 0;
    width: 30%;
    height: 30%;
  `;

  const logo = css`
    font-size: 30px;
    color: #ffff;
    margin-left: 20px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    background: #f26968;
    font-family: 'Julius Sans One', sans-serif;
    padding: 20px;
  `;

  const app = css`
    background: #f26968;
    min-height: 100vh;
    font-weight: 400;
    font-family: 'sans-serif';
    width: 100%;
    position: absolute;
  `;

  const mainTitle = css`
    color: #ffff;
    font-family: 'Julius Sans One', sans-serif;
    text-align: center;
    letter-spacing: 0.1em;
  `;

  const [startDate, setStartDate] = useState();

  const handleChange = (date) => {
    setStartDate((startDate = date));
  };

  return (
    <>
      <style>
        @import
        url('https://fonts.googleapis.com/css2?family=Muli&display=swap');
      </style>
      <div css={app}>
        <Link href={'/index'}>
          <a href="#a" css={logo}>
            Wolfi
          </a>
        </Link>
        <h1 css={mainTitle}>YOUR TRIP PLAN</h1>

        <h2 css={mainTitle}>What are your interests?:</h2>
        <DatePicker
          selected={Date}
          // onSelect={handleSelect} //when day is clicked
          onChange={handleChange} //only when value has changed
        />
        {/* <iframe
          css={iframe}
          title="Wolfi's calender"
          src="https://calendar.google.com/calendar/embed?height=600&amp;wkst=1&amp;bgcolor=%23ffffff&amp;ctz=Europe%2FVienna&amp;src=ZjQ2NWwwMHFlMHE3dmozOHNjZHE0b2lic2dAZ3JvdXAuY2FsZW5kYXIuZ29vZ2xlLmNvbQ&amp;color=%23EF6C00&amp;hl=en_GB&amp;showTabs=0&amp;showCalendars=0&amp;showPrint=0&amp;showDate=1&amp;showTitle=0"
        ></iframe> */}
        <Link href={'/readyToGo'}>
          <a href="#a">Get your plan! &#x0226B;</a>
        </Link>
      </div>
    </>
  );
}
