/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import React from 'react';
import ReactDOM from 'react-dom';
import { DatePicker, RangeDatePicker } from '@y0c/react-datepicker';

// import calendar style
// You can customize style by copying asset folder.
import '@y0c/react-datepicker/assets/styles/calendar.scss';

// Please include the locale you want to use.
// and delivery props to calendar component
import 'dayjs/locale/ko';
import 'dayjs/locale/en';
import './styles.css';

const Panel = ({ header, children }) => (
  <div style={{ height: '300px' }}>
    <h1>{header}</h1>
    <div>{children}</div>
  </div>
);

function App() {
  const onChange = (title) => (...args) => console.log(title, args);
  return (
    <div className="App">
      <Panel header="Simple DatePicker">
        <DatePicker showToday onChange={onChange('DatePicker')} />
      </Panel>
      <Panel header="Range DatePicker">
        <RangeDatePicker onChange={onChange('Range DatePicker')} />
      </Panel>
      <Panel header="Time Picker">
        <DatePicker showTimeOnly />
      </Panel>
      <Panel header="DatePicker Include Time">
        <DatePicker
          includeTime
          showToday
          onChange={onChange('DatePicker include time')}
        />
      </Panel>
    </div>
  );
}
