import { useState, useEffect } from 'react';
import Link from 'next/link';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';

export default function Header() {
  const logo = css`
    font-size: 40px;
    color: #4abdac;
    font-family: 'Karla', sans-serif;
    margin-top: 20px;
    text-decoration: none;
    margin-left: -15px;
  `;

  const navbar = css`
    width: 100%;
    display: flex;
    color: #4abdac;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    background: #ffff;
    font-family: 'Karla', sans-serif;
    margin-left: 30px;
    margin-top: 20px;
  `;

  const links = css`
    padding-right: 30px;

    border-radius: 1px;
    padding-left: 30px;
    margin-right: -10px;
    font-size: 1.5rem;
    text-decoration: none;
    color: #4abdac;
    &:hover {
      color: #4abdac;
    }
  `;

  const navbarLinkList = css`
    margin-right: 20px;
  `;
  const [user, setUser] = useState(null);
  const linkList = [{ name: 'About', url: '/about' }];

  console.log('username', user);

  if (user === null) {
    linkList.push({ name: 'Register', url: '/register' });
    linkList.push({ name: 'Login', url: '/login' });
  } else {
    linkList.push({ name: 'Logout', url: '/logout' });
  }

  useEffect(() => {
    fetch('/api/checkLogin', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      // body: JSON.stringify({ }),
    })
      .then((response) => {
        console.log('success', response);
        if (response.ok !== true) {
          throw new Error('Error fetching session');
        }
        return response.json();
      })
      .then((json) => {
        if (json === true) {
          setUser('TODO: add the user');
        }
      })
      .catch((err) => {
        console.error('error fetching session', err);
      });
  }, []);

  return (
    <div>
      <link
        href="https://fonts.googleapis.com/css2?family=Cabin+Sketch&display=swap"
        rel="stylesheet"
      ></link>
      <div css={navbar}>
        <div className="logo-header">
          <Link href={'/index'}>
            <a href="#a" css={logo}>
              Wolfi
            </a>
          </Link>
        </div>
        <div css={navbarLinkList}>
          {linkList.map((link) => {
            return (
              <Link key={link.url} href={link.url}>
                <a href="#a" css={links}>
                  {link.name}
                </a>
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
}
