import React, { useState } from 'react';
import MultiSelect from 'react-multi-select-component';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';
import Link from 'next/link';
import Footer from '../components/Footer';
import Datepicker from '../components/Datepicker';
import Header from '../components/Header';
import { START_DATE } from '@datepicker-react/hooks';
import Cookie from 'js-cookie';
import { differenceInCalendarDays, eachDayOfInterval } from 'date-fns';

//================================================================================
// Style
//================================================================================

const app = css`
  min-height: 100vh;
  font-weight: 400;
  font-family: 'Karla', sans-serif;
  width: 100%;
  position: relative;
`;

// .container {
//   min-height: 100vh;
//   padding: 0 0.5rem;
//   display: flex;
//   flex-direction: column;
//   justify-content: center;
//   align-items: center;
// }
// main {
//   padding: 5rem 0;
//   flex: 1;
//   display: flex;
//   flex-direction: column;
//   justify-content: center;
//   align-items: center;
//   padding-top: 20px;
// }

const h2 = css`
  color: #4abdac;
  font-family: 'Karla', sans-serif;
  letter-spacing: 0.1em;
  text-align: center;
  border-bottom: 2px solid #f7b733;
  border-top: 2px solid #f7b733;
  padding-top: 10px;
  padding-bottom: 10px;
`;

const dropdown = css`
  width: 50%;
  margin: auto;
`;

const readyToGo = css`
  color: #f7b733;
  font-size: 40px;
  text-decoration: none;
  font-family: 'Karla', sans-serif;
  margin-left: 40%;
`;

const calendar = css`
  margin: auto;
`;

const main = css`
  display: flex;
  flex-direction: column;
  margin-top: 50px;
`;

const divToGo = css``;

const button = css`
  width: 5%;
  font-family: 'Karla', sans-serif;
  margin: auto;
  margin-top: 40px;
  border-radius: 4px;
  color: #4abdac;
  border: 2px solid #4abdac;
  padding: 10px;
  font-weight: bold;
  margin-bottom: 30px;
`;

const selectButton = css`
  width: 70px;
  font-family: 'Karla', sans-serif;
  text-align: center;
  margin-top: 40px;
  border-radius: 4px;
  color: #4abdac;
  border: 2px solid #4abdac;
  padding: 10px;
  font-weight: bold;
  margin-bottom: 30px;
`;

const tags = css`
  height: 500px;
`;

//================================================================================
// Function
//================================================================================

export default function Interests() {
  // fetch(
  //   'https://data.wien.gv.at/daten/geo?service=WFS&request=GetFeature&version=1.1.0&typeName=ogdwien:SPAZIERPUNKTOGD &srsName=EPSG:4326&outputFormat=json',
  // )
  //   .then((res) => res.json())
  //   .then((json) => console.log(json));

  const options = [
    { value: '1', label: 'sights' },
    { value: '2', label: 'classical music' },
    { value: '3', label: 'palaces' },
    { value: '4', label: 'parks' },
    { value: '5', label: 'churchs' },
    { value: '6', label: 'markets' },
    { value: '7', label: 'coffee houses' },
    { value: '8', label: 'typical food' },
    { value: '9', label: 'vegan food' },
    { value: '10', label: 'museums' },
    { value: '11', label: 'theaters' },
    { value: '12', label: 'clubs' },
  ];

  //multiselector
  const [selected, setSelected] = useState([]);

  //calendar
  const [state, setState] = useState({
    startDate: null,
    endDate: null,
    focusedInput: START_DATE,
  });

  const result = differenceInCalendarDays(state.endDate, state.startDate);
  const resultDays = result + 1;
  alert('You have selected ' + resultDays + 'day(s)');

  const datesInInterval = eachDayOfInterval({
    start: state.startDate.toISOString().split('T')[0],
    end: state.endDate.toISOString().split('T')[0],
  });

  console.log(datesInInterval);

  ///////////////////////////////////////////////////////////////////////////////////////
  //Get days between 2 dates manually
  ///////////////////////////////////////////////////////////////////////////////////////

  // if (state.startDate !== null && state.endDate !== null) {
  //   let userFirstDate = state.startDate;
  //   let userSecondDate = state.endDate;

  //   // adjust 0 before single digit date
  //   let firstDay = ('0' + userFirstDate.getDate()).slice(-2);
  //   let secondDay = ('0' + userSecondDate.getDate()).slice(-2);

  //   // current month
  //   let firstMonth = ('0' + (userFirstDate.getMonth() + 1)).slice(-2);
  //   let secondMonth = ('0' + (userFirstDate.getMonth() + 1)).slice(-2);

  //   // current year
  //   let year = userFirstDate.getFullYear();

  //   // prints date in YYYY-MM-DD format
  //   const firstJourneyDay = firstMonth + '-' + firstDay + '-' + year;
  //   const secondJourneyDay = secondMonth + '-' + secondDay + '-' + year;

  //   const date1 = new Date(firstJourneyDay);
  //   const date2 = new Date(secondJourneyDay);
  //   const diffTime = Math.abs(date2 - date1);
  //   const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 18));
  //   alert('You have selected ' + diffDays + ' day(s)');
  // }

  const [journeyId, setJourneyId] = useState();

  return (
    <>
      <style>
        @import
        url('https://fonts.googleapis.com/css2?family=Karla&display=swap');
      </style>
      <div css={app}>
        <Header />
        <div css={main}>
          <h2 css={h2}>1. Choose your dates </h2>
          <div css={calendar}>
            <Datepicker state={state} setState={setState} />
          </div>
          <button
            css={button}
            onClick={() => {
              fetch('/api/journey', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                  startDate: state.startDate.toISOString().split('T')[0],
                  endDate: state.endDate.toISOString().split('T')[0],
                  token: Cookie.get('token'),
                }), // body data type must match "Content-Type" header
              })
                .then((response) => {
                  console.log(response);
                  return response.json();
                })
                .then((json) => {
                  console.log(json.id);
                  setJourneyId(json.id);
                });
            }}
          >
            submit
          </button>
          <div css={tags} hidden={!journeyId}>
            <h2 css={h2}>2. What are your interests:</h2>
            <div css={dropdown}>
              <pre hidden>{JSON.stringify(selected)}</pre>
              <MultiSelect
                options={options}
                value={selected}
                onChange={setSelected}
                labelledBy={'Select'}
              />
              <button
                onClick={() => {
                  fetch('/api/tags', {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json',
                      Accept: 'application/json',
                    },
                    body: JSON.stringify({
                      placeIds: selected.map((tag) => {
                        return tag.value;
                      }),
                      journeyId: journeyId,
                    }), // body data type must match "Content-Type" header
                  })
                    .then((response) => {
                      console.log(response);
                      return response.json();
                    })
                    .then((json) => {
                      console.log(json);
                      JSON.stringify(json);
                    });
                }}
                css={selectButton}
              >
                submit
              </button>
            </div>
          </div>
        </div>
        <div css={divToGo}>
          <Link href={'/journey/' + journeyId}>
            <a href="#a" css={readyToGo}>
              Get your plan! &#x0226B;
            </a>
          </Link>
        </div>
        <Footer />
      </div>
    </>
  );
}
