import React from 'react';
import Link from 'next/link';
import Header from '../../components/Header';
/** @jsx jsx */
/** @jsxFrag React.Fragment */
import { jsx, css } from '@emotion/core';

export default function userPlan({ plan }) {
  const header = css`
    width: 100%;
    background-color: #4abdac;
    display: flex;
    flex-direction: row;
  `;

  const quote = css`
    text-align: center;
    font-size: 1.5rem;
    font-family: 'Karla', sans-serif;
    margin-top: 60px;
    margin-right: 200px;
    color: #555555;
    text-align: center;
  `;

  const journey = css`
    width: 80%;
  `;
  console.log(
    'plan',
    plan.map((value) => value.name),
  );
  return (
    <div>
      <style>
        @import
        url('https://fonts.googleapis.com/css2?family=Karla:wght@400;700&display=swap');
      </style>
      <div>
        <Header />
        <div css={header}>
          <p css={quote}>Enjoy Vienna!</p>
        </div>
        <div css={journey}>
          <ul>
            {plan.map((value) => {
              return (
                <>
                  <li>Name: {value.name}</li>
                  <li>Address: {value.address}</li>
                  <img src={value.image} alt={value.name} />
                  <li>{value.description}</li>
                </>
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}

export async function getServerSideProps(context) {
  const { personalizedPlan } = await import('../../db.js');
  const plan = await personalizedPlan(context.params.id);
  console.log('this', plan);

  if (plan === null) {
    return { props: {} };
  }
  return {
    props: {
      plan,
    },
  };
}
